import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useQuery } from 'react-query';
import { useDispatch, useSelector } from 'react-redux';
import { toast } from 'react-toastify';
import { productsAPI } from '../../api/api';
import { addToCart } from '../../store/slices/cartSlice';
import LoadingSpinner from '../../components/LoadingSpinner/LoadingSpinner';
import './ProductDetailPage.css';

const ProductDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [selectedSize, setSelectedSize] = useState('');
  const [quantity, setQuantity] = useState(1);

  console.log('üîç ProductDetailPage - Product ID from URL:', id);

  const {  product, isLoading, error } = useQuery(
    ['product', id],
    () => {
      console.log('üìû Making API call for product ID:', id);
      return productsAPI.getProduct(id);
    },
    {
      enabled: !!id,
      retry: 3,
      retryDelay: 1000,
      onSuccess: (data) => {
        console.log('‚úÖ API Success - Received ', data);
      },
      onError: (error) => {
        console.error('‚ùå API Error:', error);
      }
    }
  );

  console.log('üîç Query state:', { 
    product, 
    isLoading, 
    error,
    hasData: !!product,
    hasProductData: !!product?.data,
    productData: product?.data 
  });

  const addToCartHandler = () => {
    if (!product?.data) {
      console.error('‚ùå No product data available for cart');
      toast.error('Product data not available');
      return;
    }

    if (!selectedSize) {
      toast.error('Please select a size');
      return;
    }

    dispatch(
      addToCart({
        product: product.data._id,
        name: product.data.name,
        image: product.data.image,
        price: product.data.price,
        size: selectedSize,
        qty: quantity,
        stock: product.data.stock,
      })
    );

    toast.success('Added to cart!');
  };

  const buyNowHandler = () => {
    addToCartHandler();
    navigate('/cart');
  };

  // Show loading spinner
  if (isLoading) {
    console.log('‚è≥ Still loading product...');
    return <LoadingSpinner message="Loading product details..." />;
  }

  // Show error message
  if (error) {
    console.error('üí• Error state:', error);
    return (
      <div className="product-detail-page">
        <div className="container">
          <div className="error-message">
            <h2>Product Load Error</h2>
            <p>Error: {error.message}</p>
            <p>Status: {error.response?.status}</p>
            <p>Product ID: {id}</p>
            <button className="btn" onClick={() => navigate(-1)}>
              ‚Üê Go Back
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Check if we have product data
  if (!product) {
    console.log('‚ùå No product object received');
    return (
      <div className="product-detail-page">
        <div className="container">
          <div className="error-message">
            <h2>No Product Response</h2>
            <p>The API didn't return any product data.</p>
            <button className="btn" onClick={() => navigate(-1)}>‚Üê Go Back</button>
          </div>
        </div>
      </div>
    );
  }

  if (!product.data) {
    console.log('‚ùå Product object exists but no .data property');
    console.log('üì¶ Full product object:', product);
    return (
      <div className="product-detail-page">
        <div className="container">
          <div className="error-message">
            <h2>Invalid Product Data Structure</h2>
            <p>Product data is missing the expected structure.</p>
            <pre style={{textAlign: 'left', fontSize: '12px'}}>
              {JSON.stringify(product, null, 2)}
            </pre>
            <button className="btn" onClick={() => navigate(-1)}>‚Üê Go Back</button>
          </div>
        </div>
      </div>
    );
  }

  // Extract product data
  const productData = product.data;
  console.log('‚úÖ Successfully extracted product ', productData);

  return (
    <div className="product-detail-page">
      <div className="container">
        <button className="back-btn" onClick={() => navigate(-1)}>
          ‚Üê Back
        </button>

        <div className="product-detail">
          <div className="product-image-section">
            <img src={productData.image} alt={productData.name} />
            {productData.featured && <span className="featured-badge">Featured</span>}
          </div>

          <div className="product-info-section">
            <h1>{productData.name}</h1>
            <div className="product-price">${productData.price?.toFixed(2) || '0.00'}</div>
            <div className="product-category">{productData.subcategory || productData.category}</div>

            <div className="product-description">
              <p>{productData.description || 'No description available.'}</p>
            </div>

            {productData.sizes && productData.sizes.length > 0 && (
              <div className="product-options">
                <div className="size-selector">
                  <h4>Size:</h4>
                  <div className="size-options">
                    {productData.sizes.map((size) => (
                      <button
                        key={size}
                        className={`size-btn ${selectedSize === size ? 'selected' : ''}`}
                        onClick={() => setSelectedSize(size)}
                      >
                        {size}
                      </button>
                    ))}
                  </div>
                </div>

                <div className="quantity-selector">
                  <h4>Quantity:</h4>
                  <div className="quantity-controls">
                    <button
                      onClick={() => setQuantity(Math.max(1, quantity - 1))}
                      disabled={quantity <= 1}
                    >
                      -
                    </button>
                    <span>{quantity}</span>
                    <button
                      onClick={() => setQuantity(Math.min(productData.stock || 999, quantity + 1))}
                      disabled={quantity >= (productData.stock || 999)}
                    >
                      +
                    </button>
                  </div>
                </div>
              </div>
            )}

            <div className="product-actions">
              <button
                className="btn add-to-cart-btn"
                onClick={addToCartHandler}
                disabled={(productData.stock || 0) === 0}
              >
                Add to Cart
              </button>
              <button
                className="btn btn-success buy-now-btn"
                onClick={buyNowHandler}
                disabled={(productData.stock || 0) === 0}
              >
                Buy Now
              </button>
            </div>

            <div className="product-stock">
              {(productData.stock || 0) > 0 ? (
                <span className="in-stock">
                  ‚úì In Stock ({productData.stock} available)
                </span>
              ) : (
                <span className="out-of-stock">‚úó Out of Stock</span>
              )}
            </div>

            {productData.colors && productData.colors.length > 0 && (
              <div className="product-colors">
                <h4>Available Colors:</h4>
                <div className="color-list">
                  {productData.colors.map((color) => (
                    <span key={color} className="color-tag">
                      {color}
                    </span>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetailPage;
