import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { toast } from 'react-toastify';
import { addToCart } from '../../store/slices/cartSlice';
import './ProductDetailPage.css';

const ProductDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedSize, setSelectedSize] = useState('');
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
  const fetchProduct = async () => {
    try {
      console.log('üîç Fetching product ID:', id);
      setLoading(true);
      setError(null);

      const response = await fetch(`http://localhost:5001/api/products/${id}`);
      console.log('üì° Response status:', response.status);

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('üì¶ RAW API RESPONSE:', JSON.stringify(data, null, 2));
      console.log('üì¶ data.success:', data.success);
      console.log('üì¶ data.', data.data);
      console.log('üì¶ typeof ', typeof data);

      // Show raw response in the error message for debugging
      if (!data) {
        throw new Error('No data received from server');
      }

      if (!data.success) {
        throw new Error(`API returned success: false. Message: ${data.message || 'Unknown error'}`);
      }

      if (!data.data) {
        throw new Error(`API response missing 'data' property. Full response: ${JSON.stringify(data)}`);
      }

      setProduct(data.data);
      console.log('‚úÖ Product set successfully:', data.data.name);

    } catch (err) {
      console.error('‚ùå Fetch error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (id) {
    fetchProduct();
  } else {
    setError('No product ID provided');
    setLoading(false);
  }
}, [id]);


  const addToCartHandler = () => {
    if (!product) {
      toast.error('Product data not available');
      return;
    }

    if (!selectedSize) {
      toast.error('Please select a size');
      return;
    }

    dispatch(
      addToCart({
        product: product._id,
        name: product.name,
        image: product.image,
        price: product.price,
        size: selectedSize,
        qty: quantity,
        stock: product.stock,
      })
    );

    toast.success('Added to cart!');
  };

  const buyNowHandler = () => {
    addToCartHandler();
    navigate('/cart');
  };

  // Loading state
  if (loading) {
    return (
      <div className="product-detail-page">
        <div className="container">
          <div style={{ textAlign: 'center', padding: '4rem' }}>
            <h2>Loading Product...</h2>
            <p>Product ID: {id}</p>
          </div>
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="product-detail-page">
        <div className="container">
          <div className="error-message">
            <h2>Error Loading Product</h2>
            <p><strong>Error:</strong> {error}</p>
            <p><strong>Product ID:</strong> {id}</p>
            <button className="btn" onClick={() => navigate(-1)}>
              ‚Üê Go Back
            </button>
            <button className="btn btn-primary" onClick={() => navigate('/products')}>
              Browse Products
            </button>
          </div>
        </div>
      </div>
    );
  }

  // No product data
  if (!product) {
    return (
      <div className="product-detail-page">
        <div className="container">
          <div className="error-message">
            <h2>No Product Data</h2>
            <p>Product information is not available.</p>
            <p><strong>Product ID:</strong> {id}</p>
            <button className="btn" onClick={() => navigate(-1)}>
              ‚Üê Go Back
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Success - render product
  return (
    <div className="product-detail-page">
      <div className="container">
        <button className="back-btn" onClick={() => navigate(-1)}>
          ‚Üê Back
        </button>

        {/* Debug info - remove this later */}
        <div style={{ 
          background: '#f0f0f0', 
          padding: '10px', 
          margin: '10px 0', 
          fontSize: '12px',
          borderRadius: '4px'
        }}>
          <strong>Debug Info:</strong> Product ID: {id}, Name: {product.name}, Stock: {product.stock}
        </div>

        <div className="product-detail">
          <div className="product-image-section">
            <img src={product.image} alt={product.name} />
            {product.featured && <span className="featured-badge">Featured</span>}
          </div>

          <div className="product-info-section">
            <h1>{product.name}</h1>
            <div className="product-price">${product.price?.toFixed(2) || '0.00'}</div>
            <div className="product-category">{product.subcategory || product.category}</div>

            <div className="product-description">
              <p>{product.description || 'No description available.'}</p>
            </div>

            {product.sizes && product.sizes.length > 0 && (
              <div className="product-options">
                <div className="size-selector">
                  <h4>Size:</h4>
                  <div className="size-options">
                    {product.sizes.map((size) => (
                      <button
                        key={size}
                        className={`size-btn ${selectedSize === size ? 'selected' : ''}`}
                        onClick={() => setSelectedSize(size)}
                      >
                        {size}
                      </button>
                    ))}
                  </div>
                </div>

                <div className="quantity-selector">
                  <h4>Quantity:</h4>
                  <div className="quantity-controls">
                    <button
                      onClick={() => setQuantity(Math.max(1, quantity - 1))}
                      disabled={quantity <= 1}
                    >
                      -
                    </button>
                    <span>{quantity}</span>
                    <button
                      onClick={() => setQuantity(Math.min(product.stock || 999, quantity + 1))}
                      disabled={quantity >= (product.stock || 999)}
                    >
                      +
                    </button>
                  </div>
                </div>
              </div>
            )}

            <div className="product-actions">
              <button
                className="btn add-to-cart-btn"
                onClick={addToCartHandler}
                disabled={(product.stock || 0) === 0}
              >
                Add to Cart
              </button>
              <button
                className="btn btn-success buy-now-btn"
                onClick={buyNowHandler}
                disabled={(product.stock || 0) === 0}
              >
                Buy Now
              </button>
            </div>

            <div className="product-stock">
              {(product.stock || 0) > 0 ? (
                <span className="in-stock">
                  ‚úì In Stock ({product.stock} available)
                </span>
              ) : (
                <span className="out-of-stock">‚úó Out of Stock</span>
              )}
            </div>

            {product.colors && product.colors.length > 0 && (
              <div className="product-colors">
                <h4>Available Colors:</h4>
                <div className="color-list">
                  {product.colors.map((color) => (
                    <span key={color} className="color-tag">
                      {color}
                    </span>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetailPage;
