import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001/api';

// Create axios instance
const api = axios.create({
  baseURL: API_URL,
  timeout: 10000,
  withCredentials: true
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    console.log('üì§ Making API request:', config.method?.toUpperCase(), config.url);
    const userInfo = localStorage.getItem('userInfo');
    if (userInfo) {
      const token = JSON.parse(userInfo).token;
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    console.error('‚ùå Request interceptor error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor
api.interceptors.response.use(
  (response) => {
    console.log('‚úÖ API response received:', response.status, response.config.url);
    console.log('üì¶ Response ', response.data);
    return response;
  },
  (error) => {
    console.error('‚ùå API response error:', error);
    if (error.response?.status === 401) {
      localStorage.removeItem('userInfo');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export default api;

// Products API
export const productsAPI = {
  getProducts: (params = {}) => {
    console.log('üîç productsAPI.getProducts called with params:', params);
    return api.get('/products', { params });
  },
  getProduct: (id) => {
    console.log('üîç productsAPI.getProduct called with ID:', id);
    return api.get(`/products/${id}`);
  },
  // ... other methods
};

// ... other API exports (authAPI, ordersAPI, etc.)
