import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { useMutation } from 'react-query';
import { toast } from 'react-toastify';
import { ordersAPI } from '../../api/api';
import { clearCartItems } from '../../store/slices/cartSlice';
import './CheckoutPage.css';

const CheckoutPage = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { cartItems } = useSelector((state) => state.cart);
  const { userInfo } = useSelector((state) => state.auth);

  const [shippingAddress, setShippingAddress] = useState({
    address: '',
    city: '',
    postalCode: '',
    country: 'United States',
  });

  const [paymentMethod] = useState('Razorpay');
  const [isProcessing, setIsProcessing] = useState(false);

  const createOrderMutation = useMutation(ordersAPI.createOrder);

  const subtotal = cartItems.reduce((acc, item) => acc + item.qty * item.price, 0);
  const tax = subtotal * 0.1;
  const shipping = subtotal > 100 ? 0 : 10;
  const total = subtotal + tax + shipping;

  const handleInputChange = (e) => {
    setShippingAddress({
      ...shippingAddress,
      [e.target.name]: e.target.value,
    });
  };

  const handlePayment = async (orderData) => {
    try {
      setIsProcessing(true);

      // Simulate payment processing (Demo mode)
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Create order with payment result
      const orderResponse = await createOrderMutation.mutateAsync({
        ...orderData,
        paymentResult: {
          id: `pay_demo_${Date.now()}`,
          status: 'completed',
          update_time: new Date().toISOString(),
        },
      });

      dispatch(clearCartItems());
      toast.success('Order placed successfully!');
      navigate(`/order-confirmation/${orderResponse.data._id}`);

    } catch (error) {
      toast.error('Payment failed. Please try again.');
      console.error('Payment error:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  const placeOrderHandler = async (e) => {
    e.preventDefault();

    if (!shippingAddress.address || !shippingAddress.city || !shippingAddress.postalCode) {
      toast.error('Please fill in all required shipping address fields');
      return;
    }

    const orderData = {
      orderItems: cartItems.map(item => ({
        name: item.name,
        qty: item.qty,
        image: item.image,
        price: item.price,
        size: item.size,
        product: item.product,
      })),
      shippingAddress,
      paymentMethod,
      itemsPrice: subtotal,
      taxPrice: tax,
      shippingPrice: shipping,
      totalPrice: total,
    };

    await handlePayment(orderData);
  };

  if (cartItems.length === 0) {
    navigate('/cart');
    return null;
  }

  return (
    <div className="checkout-page">
      <div className="container">
