import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { FiShoppingCart, FiUser, FiLogOut, FiPackage, FiSettings, FiChevronDown } from 'react-icons/fi';
import { logout } from '../../store/slices/authSlice';
import './Header.css';

const Header = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { userInfo } = useSelector((state) => state.auth);
  const { cartItems } = useSelector((state) => state.cart);

  const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);

  const logoutHandler = () => {
    dispatch(logout());
    navigate('/');
    setIsUserMenuOpen(false);
  };

  const cartItemsCount = cartItems.reduce((acc, item) => acc + item.qty, 0);

  // Close dropdown when clicking outside
  const handleMenuToggle = () => {
    setIsUserMenuOpen(!isUserMenuOpen);
  };

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <Link to="/" className="logo">
            <h1>FashionStore</h1>
          </Link>

          <nav className="nav">
            <ul className="nav-links">
              <li>
                <Link to="/products?category=men">Men</Link>
              </li>
              <li>
                <Link to="/products?category=women">Women</Link>
              </li>
              <li>
                <Link to="/products?category=kids">Kids</Link>
              </li>
              <li>
                <Link to="/products">All Products</Link>
              </li>
            </ul>
          </nav>

          <div className="header-actions">
            <Link to="/cart" className="cart-link">
              <FiShoppingCart />
              {cartItemsCount > 0 && (
                <span className="cart-count">{cartItemsCount}</span>
              )}
            </Link>

            {userInfo ? (
              <div className="user-menu">
                <button 
                  className="user-menu-trigger"
                  onClick={handleMenuToggle}
                >
                  <div className="user-avatar">
                    {userInfo.name.charAt(0).toUpperCase()}
                  </div>
                  <span className="username">{userInfo.name}</span>
                  <FiChevronDown className={`chevron ${isUserMenuOpen ? 'open' : ''}`} />
                </button>

                {isUserMenuOpen && (
                  <div className="user-dropdown">
                    <div className="dropdown-header">
                      <div className="user-info">
                        <div className="user-avatar-large">
                          {userInfo.name.charAt(0).toUpperCase()}
                        </div>
                        <div>
                          <div className="user-name">{userInfo.name}</div>
                          <div className="user-email">{userInfo.email}</div>
                        </div>
                      </div>
                    </div>

                    <div className="dropdown-divider"></div>

                    <div className="dropdown-links">
                      <Link 
                        to="/profile" 
                        className="dropdown-item"
                        onClick={() => setIsUserMenuOpen(false)}
                      >
                        <FiUser />
                        <span>My Profile</span>
                      </Link>

                      <Link 
                        to="/orders" 
                        className="dropdown-item"
                        onClick={() => setIsUserMenuOpen(false)}
                      >
                        <FiPackage />
                        <span>Order History</span>
                      </Link>

                      <Link 
                        to="/profile" 
                        className="dropdown-item"
                        onClick={() => setIsUserMenuOpen(false)}
                      >
                        <FiSettings />
                        <span>Account Settings</span>
                      </Link>

                      {userInfo.role === 'admin' && (
                        <>
                          <div className="dropdown-divider"></div>
                          <Link 
                            to="/admin" 
                            className="dropdown-item admin-link"
                            onClick={() => setIsUserMenuOpen(false)}
                          >
                            <FiSettings />
                            <span>Admin Dashboard</span>
                          </Link>
                        </>
                      )}

                      <div className="dropdown-divider"></div>

                      <button 
                        onClick={logoutHandler}
                        className="dropdown-item logout-item"
                      >
                        <FiLogOut />
                        <span>Sign Out</span>
                      </button>
                    </div>
                  </div>
                )}

                {/* Backdrop to close menu */}
                {isUserMenuOpen && (
                  <div 
                    className="dropdown-backdrop"
                    onClick={() => setIsUserMenuOpen(false)}
                  ></div>
                )}
              </div>
            ) : (
              <div className="auth-links">
                <Link to="/login" className="auth-link">Login</Link>
                <Link to="/register" className="auth-link register-btn">Register</Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
